import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Radio from '@mui/material/Radio';
import TranscribeForm from './components/TranscribeForm';
import VideoPlayer from './components/VideoPlayer';
import Transcript from './components/Transcript';

const theme = createTheme({
  typography: {
    fontFamily: 'Poppins, Arial, sans-serif',
  },
  palette: {
    primary: {
      main: '#3f51b5',
    },
    secondary: {
      main: '#f50057',
    },
  },
  components: {
    MuiTypography: {
      styleOverrides: {
        h3: {
          fontWeight: 600,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 500,
          borderRadius: 8,
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            borderRadius: 8,
          },
        },
      },
    },
  },
});

function App() {
  const [videoUrl, setVideoUrl] = useState('');
  const [videoId, setVideoId] = useState('');
  const [transcript, setTranscript] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [transcriptionQuality, setTranscriptionQuality] = useState('high');
  const [transcriptionMethod, setTranscriptionMethod] = useState('auto');

  const handleSubmit = (url) => {
    setVideoUrl(url);
    setError(null);
    const id = extractVideoId(url);
    
    if (!id) {
      setError('Invalid YouTube URL. Please provide a valid YouTube video URL.');
      return;
    }
    
    setVideoId(id);
    fetchTranscript(id);
  };

  const extractVideoId = (url) => {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return match && match[2].length === 11 ? match[2] : null;
  };

  const fetchTranscript = async (videoId) => {
    try {
      setLoading(true);
      setTranscript([]);
      
      // Add quality and method parameters to the API request
      const apiUrl = `/api/transcript?videoId=${videoId}&quality=${transcriptionQuality}&method=${transcriptionMethod}`;
      const response = await fetch(apiUrl);
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch transcript');
      }
      
      const data = await response.json();
      
      if (!data.transcript || data.transcript.length === 0) {
        throw new Error('No transcript data available for this video');
      }
      
      setTranscript(data.transcript);
    } catch (error) {
      console.error('Error fetching transcript:', error);
      setError(error.message || 'Failed to fetch transcript');
    } finally {
      setLoading(false);
    }
  };

  const handleTimeUpdate = (time) => {
    setCurrentTime(time);
  };

  const handleQualityChange = (event) => {
    setTranscriptionQuality(event.target.value);
  };

  const handleMethodChange = (event) => {
    setTranscriptionMethod(event.target.value);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="md">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h3" component="h1" gutterBottom className="app-title">
            YouTube Transcription
          </Typography>
          
          <Box sx={{ mb: 4, display: 'flex', justifyContent: 'center', gap: 2, flexWrap: 'wrap' }}>
            <FormControl sx={{ minWidth: 150 }}>
              <InputLabel id="quality-label">Accuracy Level</InputLabel>
              <Select
                labelId="quality-label"
                value={transcriptionQuality}
                label="Accuracy Level"
                onChange={handleQualityChange}
                size="small"
              >
                <MenuItem value="standard">Standard</MenuItem>
                <MenuItem value="high">High</MenuItem>
                <MenuItem value="premium">Premium (AI)</MenuItem>
              </Select>
            </FormControl>
            
            <FormControl>
              <RadioGroup
                row
                value={transcriptionMethod}
                onChange={handleMethodChange}
              >
                <FormControlLabel 
                  value="auto" 
                  control={<Radio size="small" />} 
                  label="Auto-detect best method" 
                />
                <FormControlLabel 
                  value="ai" 
                  control={<Radio size="small" />} 
                  label="Force AI transcription" 
                />
              </RadioGroup>
            </FormControl>
          </Box>
          
          <TranscribeForm onSubmit={handleSubmit} isLoading={loading} />
          
          {error && (
            <Alert severity="error" sx={{ mb: 4 }}>
              {error}
            </Alert>
          )}
          
          {videoId && (
            <Box className="player-container">
              <VideoPlayer 
                videoId={videoId} 
                onTimeUpdate={handleTimeUpdate} 
              />
            </Box>
          )}
          
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
              <CircularProgress />
            </Box>
          ) : transcript.length > 0 ? (
            <Transcript 
              transcript={transcript} 
              currentTime={currentTime} 
            />
          ) : null}
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
